    function loop(){
    
        frage = document.getElementById('tdLabelFrageNr').innerHTML;
        frage = String(/\d{1,5}/.exec(frage));
    
        if(document.getElementById('divResultPanel').style['display'] == "table"){
            if(document.getElementById('divResultPanel').style['background-color'] == "rgb(255, 0, 0)"){
                jsonData[frage] = {};
                jsonData[frage][document.getElementById('tdLabelAnt1Text').textContent.trim()] = (document.getElementById('divLabelAnt1Text').style['color'] == "rgb(0, 128, 0)") ? true : false;
                jsonData[frage][document.getElementById('tdLabelAnt2Text').textContent.trim()] = (document.getElementById('divLabelAnt2Text').style['color'] == "rgb(0, 128, 0)") ? true : false;
                jsonData[frage][document.getElementById('tdLabelAnt3Text').textContent.trim()] = (document.getElementById('divLabelAnt3Text').style['color'] == "rgb(0, 128, 0)") ? true : false;
                jsonData[frage][document.getElementById('tdLabelAnt4Text').textContent.trim()] = (document.getElementById('divLabelAnt4Text').style['color'] == "rgb(0, 128, 0)") ? true : false;
                console.log("Added Question: " + frage);
            }
            document.getElementById('cmdSpeedWeiter').click();
        }else{
            if (jsonData[frage] == undefined){
                console.log('Question not found: ' + String(frage));
                SpeedAntClick(1,'SpeedAntR1img');
                SpeedAntClick(2,'SpeedAntR2img');
                SpeedAntClick(3,'SpeedAntR3img');
                SpeedAntClick(4,'SpeedAntR4img');
                document.getElementById('cmdSpeedWeiter').click();
            } else {
                if (frage != prev_question) {
                    prev_question = frage;
            
                    if (jsonData[frage][document.getElementById('tdLabelAnt1Text').textContent.trim()]){
                        SpeedAntClick(1,'SpeedAntR1img');
                    }
                
                    if (jsonData[frage][document.getElementById('tdLabelAnt2Text').textContent.trim()]){
                        SpeedAntClick(2,'SpeedAntR2img');
                    }
                
                    if (jsonData[frage][document.getElementById('tdLabelAnt3Text').textContent.trim()]){
                        SpeedAntClick(3,'SpeedAntR3img');
                    }
                
                    if (jsonData[frage][document.getElementById('tdLabelAnt4Text').textContent.trim()]){
                        SpeedAntClick(4,'SpeedAntR4img');
                    }
            
                    document.getElementById('cmdSpeedWeiter').click();
            
                }
            }
        }
    
        if(checkProgress()){
            printCode();
        }
    }
    
    function checkProgress(){
        var parent = parseInt(getComputedStyle(document.getElementById('divBarFrageIndex'))['width'], 10);
        var child = parseInt(getComputedStyle(document.getElementById('divProgBarFrageIndex1'))['width'], 10);
        if (child >= parent-1){
            return true;
        }else {
            return false;
        }
    }
    
    function printCode(){
        var win = window.open("", "Title", "toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=yes,width=780,height=200,top="+(screen.height-400)+",left="+(screen.width-840));
        var reg = /(.*data = ")(.*\})(".*)/;
        win.document.body.innerHTML = String(Super).replace(reg,"$1" + JSON.stringify(jsonData).replaceAll("\"","\\\"") + "$3") + "Super();";
    }
    
